import React, { useRef, useState } from 'react';
import { createChart } from 'lightweight-charts';

const StockChart = () => {
  const chartContainerRef = useRef();
  const chartRef = useRef(null);
  const [symbol, setSymbol] = useState('');

  const fetchAndRenderChart = async (ticker) => {
    try {
      const res = await fetch(`http://localhost:3000/api/stock-data?symbol=${ticker}`);
      const json = await res.json();
      const data = json.data;

      if (!data || data.s !== 'ok') {
        alert('Invalid symbol or no data found.');
        return;
      }

      const formattedData = data.t.map((time, i) => ({
        time: time,
        open: data.o[i],
        high: data.h[i],
        low: data.l[i],
        close: data.c[i],
      }));

      // Clear previous chart if it exists
      if (chartRef.current) {
        chartRef.current.remove();
      }

      const chart = createChart(chartContainerRef.current, {
        width: chartContainerRef.current.clientWidth,
        height: 400,
      });

      chart.addCandlestickSeries().setData(formattedData);
      chartRef.current = chart;
    } catch (err) {
      console.error('Failed to load chart:', err);
      alert('Error fetching data.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const ticker = symbol.trim().toUpperCase();
    if (ticker) {
      fetchAndRenderChart(ticker);
    }
  };

  return (
    <div className="card mt-4 shadow-sm">
      <div className="card-body">
        <h5 className="card-title">Stock Chart Viewer</h5>
        <form onSubmit={handleSubmit} className="form-inline mb-3">
          <input
            type="text"
            className="form-control mr-2"
            placeholder="Enter stock symbol (e.g. AAPL)"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value)}
            required
            style={{ maxWidth: '300px' }}
          />
          <button type="submit" className="btn btn-outline-dark">
            Load Chart
          </button>
        </form>
        <div
          ref={chartContainerRef}
          style={{ width: '100%', height: '400px' }}
        ></div>
      </div>
    </div>
  );
};

export default StockChart;
