import React, { useRef, useState } from 'react';
import { createChart } from 'lightweight-charts';
import {
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Box,
  Stack
} from '@mui/material';

const StockChart = () => {
  const chartContainerRef = useRef(null);
  const chartRef = useRef(null);
  const [symbol, setSymbol] = useState('');

  const fetchAndRenderChart = async (ticker) => {
    try {
      const res = await fetch(`http://localhost:3000/api/stock-data?symbol=${ticker}`);
      const json = await res.json();
      const data = json.data;

      if (!data || !Array.isArray(data) || data.length === 0) {
        alert('Invalid symbol or no data found.');
        return;
      }

      const chartContainer = chartContainerRef.current;
      if (!chartContainer) {
        console.error("Chart container not found!");
        return;
      }

      // Clear previous chart if it exists
      if (chartRef.current) {
        chartRef.current.remove();
        chartRef.current = null;
      }

      // Create new chart
      const chart = createChart(chartContainer, {
        width: chartContainer.clientWidth,
        height: 400,
        layout: {
          backgroundColor: '#ffffff',
          textColor: '#000',
        },
        grid: {
          vertLines: { color: '#eee' },
          horzLines: { color: '#eee' },
        },
        crosshair: {
          mode: 1,
        },
        priceScale: {
          borderColor: '#ccc',
        },
        timeScale: {
          borderColor: '#ccc',
        },
      });

      const series = chart.addCandlestickSeries();
      series.setData(data);

      chartRef.current = chart;
    } catch (err) {
      console.error('Failed to load chart:', err);
      alert('Error fetching chart data.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const ticker = symbol.trim().toUpperCase();
    if (ticker) {
      fetchAndRenderChart(ticker);
    }
  };

  return (
    <Card sx={{ mt: 4, boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          Real-Time Stock Chart Viewer
        </Typography>

        <Box component="form" onSubmit={handleSubmit} sx={{ mb: 3 }}>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
            <TextField
              label="Enter stock symbol (e.g. AAPL)"
              variant="outlined"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value)}
              required
            />
            <Button
              type="submit"
              variant="contained"
              color="primary"
              sx={{ minWidth: '120px' }}
            >
              Load Chart
            </Button>
          </Stack>
        </Box>

        <Box
          ref={chartContainerRef}
          sx={{
            width: '100%',
            height: 400,
            borderRadius: 1,
            border: '1px solid #ddd',
          }}
        />
      </CardContent>
    </Card>
  );
};

export default StockChart;
